mapper.select:
select DATE_FORMAT(order_date,
'%Y-%m-%d') as col1,
 ifnull(sum(total_hours),
0) as col2
 from order_info 
 where 1=1 

 and order_date  >= #{orderDate1}


 and order_date  <= #{orderDate2}

 and order_status!=#{orderStatus}

 and user_id=#{userId}

group by DATE_FORMAT(order_date,'%Y-%m-%d')
order by col1 asc
