mapper.select:
  select

    distinct


  from order_info




    order by ${orderByClause}


limit ${startRow},${pageRows}

mapper.update:
update order_info
set 
				order_no = #{record.orderNo,jdbcType=VARCHAR},	desk_id = #{record.deskId,jdbcType=INTEGER},	order_date = #{record.orderDate,jdbcType=VARCHAR},	start_time = #{record.startTime,jdbcType=INTEGER},	end_time = #{record.endTime,jdbcType=INTEGER},	total_hours = #{record.totalHours,jdbcType=INTEGER},	price = #{record.price,jdbcType=DOUBLE},	total_amount = #{record.totalAmount,jdbcType=DOUBLE},	user_id = #{record.userId,jdbcType=INTEGER},	order_status = #{record.orderStatus,jdbcType=INTEGER},	create_time = #{record.createTime,jdbcType=VARCHAR}

update order_info
set 
 			order_no = #{orderNo,jdbcType=VARCHAR},	desk_id = #{deskId,jdbcType=INTEGER},	order_date = #{orderDate,jdbcType=VARCHAR},	start_time = #{startTime,jdbcType=INTEGER},	end_time = #{endTime,jdbcType=INTEGER},	total_hours = #{totalHours,jdbcType=INTEGER},	price = #{price,jdbcType=DOUBLE},	total_amount = #{totalAmount,jdbcType=DOUBLE},	user_id = #{userId,jdbcType=INTEGER},	order_status = #{orderStatus,jdbcType=INTEGER},	create_time = #{createTime,jdbcType=VARCHAR}
